(171, 13, NULL, 2, 'Nhận định không đúng là:', 'a:4:{i:0;a:3:{s:7:"content";s:235:"trong trồng trọt, đối với mỗi loại hoocmôn sử dụng cần xác định nồng độ thích hợp là nồng độ phù hợp với mọi loại cây trồng trong các điều kiện môi trường sống ổn định.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:186:"trong nông nghiệp, khi sử dụng hoocmôn cần chú ý tính chất đối kháng, hỗ trợ giữa các hoocmôn và điều kiện sinh thái có liên quan đến cây trồng.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:194:"người ta sử dụng các hoocmôn sinh trưởng trong nông nghiệp để tăng năng suất cây trồng, kéo dài hoặc rút ngắn thời gian thu hoạch, thu hoạch đồng loạt.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:221:"trong đời của cây, khi các hoocmôn kích thích chiếm ưu thế (ở giai đoạn non), cây sinh trưởng sinh dưỡng mạnh. khi hoocmôn ức chế chiếm ưu thế, cây sinh trưởng sinh sản mạnh.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 234, NULL, NULL),
(172, 47, NULL, 2, 'Nhân bản vô tính ở động vật không giúp:', 'a:4:{i:0;a:3:{s:7:"content";s:55:"tạo ra các loài vật nuôi có ưu thế lai cao. ";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:96:"tạo ra các cá thể động vật có đặc điểm di truyền giống hệt dạng gốc.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:50:"nhân bản các loài động vật quí hiếm. ";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:89:"mở ra triển vọng lớn đối với việc giải quyết các mô ghép khi cần.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 94, NULL, NULL),
(173, 13, NULL, 2, 'Trong cơ chế hình thành điện thế hoạt động, sự khử cực và đảo cực xảy ra do ở giai đoạn này', 'a:4:{i:0;a:3:{s:7:"content";s:75:"cổng Na+ mở nên ion Na+  từ trong đi ra ngoài tế bào tế bào.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:67:"cổng Na+ mở nên ion Na+  từ ngoài đi vào trong tế bào.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:72:"cổng K+ mở ra, ion K+ khuếch tán từ trong ra ngoài tế bào. ";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:74:"cổng K+ mở ra, ion K+ khuếch tán từ ngoài vào trong tế bào. ";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 20, NULL, NULL),
(174, 47, NULL, 2, 'Biện pháp nào sau đây không phải ứng dụng những hiểu biết về tập tính của động vật trong phòng trừ sâu hại?', 'a:4:{i:0;a:3:{s:7:"content";s:73:"Nuôi bọ rùa rồi thả vào đồng ruộng để diệt rệp cam. ";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:46:"Sử dụng ong mắt đỏ để diệt sâu.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:109:"Sử dụng con đực bất thụ để hạn chế và tiêu diệt các quần thể sâu bọ gây hại.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:69:"Phun thuốc hóa học để diệt nhiều quần thể sâu hại.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 75, NULL, NULL),
