(300, 58, NULL, 2, 'Một trong những phương hướng cơ bản của khoa học công nghệ là gì?', 'a:4:{i:0;a:3:{s:7:"content";s:34:"Cung cấp luận cứ khoa học.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:54:"Tạo thị trường cho khoa học và công nghệ.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:66:"Giải đáp kịp thời vấn đề lí luận và thực tiễn";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:62:"Nghiên cứu, ứng dụng và chuyển giao cho công nhân.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 113, NULL, NULL),
