(302, 59, NULL, 2, 'Phân bổ dân cư là gì?', 'a:4:{i:0;a:3:{s:7:"content";s:51:"Là sự phân chia tổng số dân theo khu vực";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:80:"Là sự phân chia tổng số dân theo khu vực, vùng địa lí kinh tế.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:117:"Là sự phân chia tổng số dân theo khu vực, vùng địa lí kinh tế hoặc một đơn vị hành chính.";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:89:"Là sự phân chia tổng số dân theo khu vực hoặc một đơn vị hành chính.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 152, NULL, NULL),
(303, 59, NULL, 2, 'Một trong những mục tiêu của chính sách giải quyết việc là  nước ta là gì?', 'a:4:{i:0;a:3:{s:7:"content";s:33:"Giảm tỉ lệ thất nghiệp.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:51:"Tăng tỉ lệ lao động qua đào tạo nghề.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:43:"Sử dụng có hiệu quả nguồn vốn.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:85:"Giảm tỉ lệ thất nghiệp, tăng tỉ lệ lao động qua đào tạo nghề.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 60, NULL, NULL),
(304, 59, NULL, 2, 'Nhiệm vụ của giáo dục – đào tạo nước ta hiện nay là gì?', 'a:4:{i:0;a:3:{s:7:"content";s:39:"Xây dựng và bảo vệ Tổ quốc.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:72:"Nâng cao dân trí, đào tạo nhân lực, bồi dưỡng nhân tài.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:81:"Phục vụ sự nghiệp công nghiệp hóa, hiện đại hóa đất nước.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:20:"Cả a, b, c đúng.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 100, NULL, NULL),
(305, 59, NULL, 2, 'Để nâng cao tiềm lực khoa học công nghệ nước ta cần phải có biện pháp như thế nào?', 'a:4:{i:0;a:3:{s:7:"content";s:90:"Khai thác mọi tiềm năng sáng tạo trong nghiên cứu khoa học và công nghệ.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:50:"Tăng cường cơ sở vật chất kỉ thuật.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:52:"Tạo ra thị trường cạnh tranh bình đẳng.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:61:"Nâng cao số lượng đội ngũ nghiên cứu khoa học.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 64, NULL, NULL),
(306, 59, NULL, 2, 'Nền văn hóa đậm đà bản sắc dân tộc là nền văn hóa như thế nào?', 'a:4:{i:0;a:3:{s:7:"content";s:55:"Nền văn hóa tạo ra sức sống của dân tộc.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:52:"Nền văn hóa thể hiện bản lĩnh dân tộc.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:96:"Nền văn hóa chứa đựng những yếu tố tạo ra sức sống, bản lĩnh dân tộc.";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:45:"Nền văn hóa kế thừa truyền thống.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 23, NULL, NULL),
(307, 59, NULL, 2, 'Một trong những phương hướng cơ bản để xây dựng nền văn hóa tiên tiến đậm đà bản sắc dân tộc là gì?', 'a:4:{i:0;a:3:{s:7:"content";s:44:"Đổi mới cơ chế quản lí văn hóa.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:52:"Tập trung vào nhiệm vụ xây dựng văn hóa.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:49:"Tạo môi trường cho văn hóa phát triển.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:122:"Nâng cao hiểu biết và mức hưởng thụ văn hóa, phát huy tiềm năng sáng tạo văn hóa trong nhân dân.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 174, NULL, NULL),
(308, 61, NULL, 2, 'Quá độ lên CNXH ở Việt Nam bỏ qua chế dộ TBCN được hiểu như thế nào?', 'a:4:{i:0;a:3:{s:7:"content";s:76:"Bỏ qua toàn bộ sự phát triển trong giai đọa phát triển TBCN.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:87:"Bỏ qua việc xác lập địa vị thống trị của quan hệ sản xuất TBCN.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:65:"Bỏ qua việc sử dụng thành quả khoa học kỉ thuật.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:36:"Bỏ qua phương thức quản lí.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 43, NULL, NULL),
(309, 61, NULL, 2, 'Làm thế nào để kế thừa và phát huy những di sản, truyền thống văn hóa của dân tộc?', 'a:4:{i:0;a:3:{s:7:"content";s:136:"Kế thừa, phát huy những giá trị tinh thần, đạo đức, thẩm mỹ, văn hóa, thuần phong mỹ tục của dân tộc.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:118:"Bảo tồn, tôn tạo các di tích lịch sử, di sản văn hóa và danh lam thắng cảnh của đất nước";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:256:"Kế thừa, phát huy những giá trị tinh thần, đạo đức, thẩm mỹ, văn hóa, thuần phong mỹ tục của dân tộc, bảo tồn, tôn tạo các di tích lịch sử, di sản văn hóa và danh lam thắng cảnh của đất nước.";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:189:"Kế thừa, phát huy những giá trị tinh thần, đạo đức, bảo tồn, tôn tạo các di tích lịch sử, di sản văn hóa và danh lam thắng cảnh của đất nước.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 202, NULL, NULL),
(310, 22, NULL, 2, 'Anh T đầu tư 15 triệu đồng vào mua một chiếc máy cày để đi cày thuê cho bà con nông dân. Cứ mỗi ngày cày thuê về, anh thu được từ bà con nông dân 300 ngàn đồng, trừ chi phí anh thu lãi 200 ngàn đồng. Hãy xác định tổng vốn đầu tư của Anh T.', 'a:4:{i:0;a:3:{s:7:"content";s:11:"15 triệu.";s:7:"correct";i:1;s:5:"order";i:118;}i:1;a:3:{s:7:"content";s:33:"15 triệu 1 trăm ngàn đồng.";s:7:"correct";i:0;s:5:"order";i:94;}i:2;a:3:{s:7:"content";s:33:"15 triệu 2 trăm ngàn đồng.";s:7:"correct";i:0;s:5:"order";i:116;}i:3;a:3:{s:7:"content";s:33:"15 triệu 3 trăm ngàn đồng.";s:7:"correct";i:0;s:5:"order";i:43;}}', 0, 44744, 117, NULL, NULL),
