(312, 22, NULL, 2, 'Để bảo quản lạp xưởng khô được lâu thì phương pháp đúng nhất là', 'a:4:{i:0;a:3:{s:7:"content";s:107:"cho 1 li nhỏ rượu trắng vào hộp, xếp lạp xưởng xung quanh và bảo quản nơi khô ráo.";s:7:"correct";i:1;s:5:"order";i:50;}i:1;a:3:{s:7:"content";s:71:"cho lạp xưởng vào đậu tương, đậu đen để bảo quản.";s:7:"correct";i:0;s:5:"order";i:73;}i:2;a:3:{s:7:"content";s:61:"quấn lạp xưởng trong giấy báo để nơi khô ráo.";s:7:"correct";i:0;s:5:"order";i:213;}i:3;a:3:{s:7:"content";s:121:"cho 1 li nhỏ rượu trắng vào hộp, xếp lạp xưởng xung quanh và đưa vào tủ lạnh để bảo quản.";s:7:"correct";i:0;s:5:"order";i:87;}}', 0, 12015, 65, NULL, NULL),
(313, 12, NULL, 2, 'Ở người, thời gian mỗi chu kỳ hoạt động của tim trung bình  là:', 'a:4:{i:0;a:3:{s:7:"content";s:110:"0,12 giây, trong đó tâm nhĩ co 0,2 giây, tâm thất co 0,4 giây, thời gian dãn chung là 0,6 giây.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:109:"0,6 giây, trong đó tâm nhĩ co 0,1 giây, tâm thất co 0,2 giây, thời gian dãn chung là 0,6 giây.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:109:"0,8 giây, trong đó tâm nhĩ co 0,1 giây, tâm thất co 0,3 giây, thời gian dãn chung là 0,4 giây.";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:107:"1 giây, trong đó tâm nhĩ co 0,2 giây, tâm thất co 0,3 giây, thời gian dãn chung là 0,5 giây.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 128, NULL, NULL),
