(708, 51, NULL, 2, 'Căn cứ vào các tiêu chí nào sau đây để phân loại các đô thị ở nước ta?', 'a:4:{i:0;a:3:{s:7:"content";s:81:"Số dân, chức năng, mật độ dân số, tỉ lệ dân phi nông nghiệp";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:94:" Tỉ lệ dân phi nông nghiệp, mật độ dân số, các khu công nghiệp tập trung";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:83:"các khu công nghiệp tập trung, chức năng, mật độ dân số, số dân";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:81:"Mật độ dân số, tỉ lệ dân phi nông nghiệp, diện tích, số dân";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 161, NULL, NULL),
