(711, 51, NULL, 2, 'Phát biểu nào sau đây không đúng với ảnh hưởng của đô thị hóa đến phát triển kinh tế - xã hội nước ta?', 'a:4:{i:0;a:3:{s:7:"content";s:84:"Tác động mạnh tới quá trình chuyển dịch cơ cấu kinh tế nước ta";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:101:"Ảnh hưởng rất lớn đến sự phát triển kinh tế - xã hội của các địa phương";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:95:"Sử dụng không nhiều lực lượng lao động có trình độ chuyên môn kĩ thuật";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:84:"Có khả năng tạo ra nhiều việc làm và thu nhập cho người lao động";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 15, NULL, NULL),
(712, 51, NULL, 2, 'Nguyên nhân làm cho quá trình đô thị hóa hiện nay ở nước ta phát triển là:', 'a:4:{i:0;a:3:{s:7:"content";s:54:"nền kinh tế chuyển qua cơ chế thị trường";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:38:"hội nhập quốc tế và khu vực";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:54:"quá trình công nghiệp hóa được đẩy mạnh";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:51:"thu hút được nhiều đầu tư nước ngoài";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 201, NULL, NULL),
(713, 15, NULL, 2, 'Điểm nào sau đây biểu hiện chủ yếu nhất cho một nền kinh tế tăng trưởng bền vững?', 'a:4:{i:0;a:3:{s:7:"content";s:95:"Cơ cấu giữa các ngành, các thành phần kinh tế và các vùng lãnh thổ hợp lí";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:97:"Cơ cấu giữa các ngành, các thành phần kinh tế hợp lí và phân bố rộng khắp";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:97:"Cơ cấu ngành và các vùng lãnh thổ hợp lí, kinh tế ngoài Nhà nước chủ đạo";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:102:"Cơ cấu các thành phần kinh tế và các vùng lãnh thổ hợp lí, dịch vụ phát triển";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 198, NULL, NULL),
(714, 15, NULL, 2, 'Vấn đề có ý nghĩa chiến lược quan trọng trong quá trình công nghiệp hóa, hiện đại hóa đất nước là:', 'a:4:{i:0;a:3:{s:7:"content";s:87:"Thúc đẩy sự chuyển dịch cơ cấu kinh tế và phân bố lại sản xuất";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:100:"Xác định cơ cấu kinh tế hợp lí và thúc đẩy sự chuyển dịch cơ cấu kinh tế";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:94:"Xác định cơ cấu kinh tế hợp lí và tổ chức phân công lao động lãnh thổ";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:88:"Thúc đẩy sự chuyển dịch cơ cấu kinh tế và sản xuất chuyên môn hóa";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 134, NULL, NULL),
(715, 15, NULL, 2, 'Cơ cấu ngành kinh tế trong GDP ở nước ta đang có sự chuyển dịch theo hướng:', 'a:4:{i:0;a:3:{s:7:"content";s:63:"Tăng tỉ trọng khu vực III,Tăng tỉ trọng khu vực I";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:64:"Tăng tỉ trọng khu vực II, giảm tỉ trọng khu vực I";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:68:"giảm tỉ trọng khu vực I và giảm tỉ trọng khu vực II";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:67:"giảm tỉ trọng khu vực II, giảm tỉ trọng khu vực III";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 77, NULL, NULL),
