(717, 13, NULL, 2, 'Ở động vật, sự hình thành giao tử ở cơ thể đực và cái khác nhau như thế nào?', 'a:4:{i:0;a:3:{s:7:"content";s:187:"1 tế bào sinh tinh trùng giảm phân và hình thành 2 tế bào đơn bội gồm 1 thể cực, 1 tinh trùng còn 1 tế bào sinh trứng giảm phân tạo 2 tế bào trứng.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:187:"1 tế bào sinh tinh trùng giảm phân và hình thành 4 tinh trùng còn 1 tế bào sinh trứng giảm phân tạo 4 tế bào đơn bội gồm 3 thể cực, 1 tế bào trứng.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:187:"1 tế bào sinh tinh trùng giảm phân và hình thành 2 tinh trùng còn 1 tế bào sinh trứng giảm phân tạo 2 tế bào đơn bội gồm 1 thể cực, 1 tế bào trứng.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:187:"1 tế bào sinh tinh trùng giảm phân và hình thành 4 tế bào đơn bội gồm 3 thể cực, 1 tinh trùng còn 1 tế bào sinh trứng giảm phân tạo 4 tế bào trứng.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 193, NULL, NULL),
(718, 47, NULL, 2, 'Cây ăn quả lâu năm người ta thường chiết cành, vì ', 'a:4:{i:0;a:3:{s:7:"content";s:80:"tránh sâu bệnh gây hại, ít tốn công chăm sóc, tốt hơn cây mẹ.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:93:"rút ngắn thời gian sinh trưởng, sớm thu hoạch, biết trước đặc tính cây.";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:75:"dễ trồng và ít tốn công chăm sóc, tránh sâu bệnh gây hại.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:98:"ít tốn diện tích đất trồng, tránh sâu bệnh gây hại, ít tốn công  chăm sóc.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 0, NULL, NULL),
(719, 13, NULL, 2, 'Không thuộc những đặc trưng của sinh sản hữu tính là', 'a:4:{i:0;a:3:{s:7:"content";s:80:"tạo ra hậu thế luôn thích nghi với môi trường sống ổn định.";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:78:"luôn có quá trình hình thành và hợp nhất của các các giao tử.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:62:"luôn có sự trao đổi, tái tổ hợp của hai bộ gen";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:52:"tạo ra thế hệ con đa dạng về di truyền.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 186, NULL, NULL),
(720, 13, NULL, 2, 'Động vật có thể nhận biết được các kích thích khác nhau do thông tin từ các thụ quan được gửi về trung ương dưới dạng các xung thần kinh đã được mã hoá bằng ', 'a:4:{i:0;a:3:{s:7:"content";s:70:"tần số xung, vị trí và số lượng nơron bị hưng phấn.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:113:"  số lượng nơron bị hưng phấn, kích thích càng mạnh số nơtron bị kích thích càng nhiều.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:72:"tần số xung, kích thích càng mạnh tần số xung càng thấp.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:69:"tần số xung, kích thích càng mạnh tần số xung càng cao.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 165, NULL, NULL),
(721, 13, NULL, 2, 'Không đúng khi nói về sinh trưởng và phát triển ở động vật là:', 'a:4:{i:0;a:3:{s:7:"content";s:120:"ở đa số động vật cơ thể trước tuổi phát dục lớn chậm, sau tuổi phát dục lớn rất nhanh.";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:99:"tốc độ sinh trưởng diễn ra không đồng đều ở các giai đoạn sống khác nhau.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:112:"sinh trưởng và phát triển của cơ thể luôn liên quan mật thiết với nhau, đan xen lẫn nhau.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:95:"sinh trưởng tạo tiền đề cho phát triển, phát triển thúc đẩy sinh trưởng.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 17, NULL, NULL),
(722, 47, NULL, 2, 'Nhận định không đúng là:', 'a:4:{i:0;a:3:{s:7:"content";s:164:"các chất độc hại, chất gây đột biến đều có tác động làm sai lệch sự phát triển và gây nên quái thai dị hình ở động vật";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:133:".  phụ nữ mang thai sống trong môi trường không khí ô nhiễm sẽ sinh con có chỉ số thông minh (IQ) thấp hơn.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:191:"các nhân tố môi trường như lượng O2 ‚ CO2, nước muối khoáng, ánh sáng, nhiệt độ ...đều gây ảnh hưởng lên sinh trưởng phát triển của động vật.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:172:"các loài động vật hoang dã có sức chống chịu rất tốt nên môi trường ô nhiễm ít ảnh hưởng đến sinh trưởng, phát triển của chúng.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 97, NULL, NULL),
(723, 47, NULL, 2, 'Nhận định không đúng là:', 'a:4:{i:0;a:3:{s:7:"content";s:220:"Chất độc màu da cam có tác dụng gây rụng lá ở thực vật và chứa dioxin có khả năng gây hại cho người như chất phóng xạ khi tích tụ trong cơ thể người gây đột biến gen.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:217:"sử dụng hoocmôn thực vật với nồng độ thích hợp giúp tăng năng suất cây trồng, nếu sử dụng với nồng độ quá cao có thể phá hủy hay gây chết mô và tế bào sinh vật.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:184:"Không dùng auxin nhân tạo với nông phẩm trực tiếp làm thức ăn vì chúng không có enzim phân giải nên tích lũy và gây độc cho người và động vật.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:212:"Đối với cây ngày dài để kích thích cây ra hoa trong điều kiện ngày ngắn thì bắt buộc phải tạo ngày dài nhân tạo bằng cách chiếu sáng liên tục từ chiều tới khuya.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 179, NULL, NULL),
