(730, 46, NULL, 2, 'Nhận định không đúng là:', 'a:4:{i:0;a:3:{s:7:"content";s:226:"chất dinh dưỡng trong thức ăn là nguyên liệu để tăng số lượng, kích thước tế bào, mô, cơ quan. Chất dinh dưỡng trong thức ăn còn là nguồn năng lượng cho các hoạt động sống.";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:257:"động vật đằng nhiệt khi trời lạnh cơ thể mất nhiều nhiệt, làm tăng chuyển hoá để bù đắp lượng nhiệt bị mất , vì vậy nếu thiếu thức ăn thì cơ thể gầy sút, dễ mắc bệnh, thậm chí là chết.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:132:"trong ánh sáng mặt trời có tia tử ngoại biến tiền D thành vitamin D, ánh sáng ảnh hưởng đến nhiệt độ ";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:232:"động vật biến nhiệt, khi nhiệt độ xuống thấp làm thân nhiệt giảm nên quá trình chuyển hoá trong cơ thể tăng nên các hoạt động sống tăng, vì vậy sinh trưởng, phát triển nhanh hơn.";s:7:"correct";i:1;s:5:"order";i:3;}}', 0, 65535, 30, NULL, NULL),
(731, 46, NULL, 2, 'Trong sinh sản hữu tính', 'a:4:{i:0;a:3:{s:7:"content";s:73:"luôn gắn liền với quá trình nguyên phân hình thành bào tử";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:53:"cơ thể con sinh ra có hệ gen luôn giống nhau";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:55:"luôn có sự trao đổi, tái tổ hợp 2 bộ gen.";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:59:"cơ thể con sinh ra có hệ gen luôn giống cây mẹ.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 102, NULL, NULL),
