(734, 46, NULL, 2, 'Nhận định không đúng là:', 'a:4:{i:0;a:3:{s:7:"content";s:215:"khai thác, đánh bắt động vật vào mùa sinh sản là hiệu quả và ít gây tổn hại đến các quần thể động vật nhất vì lúc này số lượng động vật tăng lên rất nhiều.";s:7:"correct";i:1;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:237:"việc xâm phạm rừng, làm biến đổi môi trường sống của các loài theo hướng tiêu cực đang là một trong những nguyên nhân chính khiến nhiều loài động vật trong sách Đỏ bị tuyệt chủng.";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:128:"bảo tồn các loài động vật hoang dã, động vật quý hiếm còn để duy trì sự cân bằng của sinh thái.";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:155:"biến đổi khí hậu do môi trường ô nhiễm là một trong những nguyên nhân quan trọng làm suy giảm của nhiều loài động vật.";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 105, NULL, NULL),
