(741, 15, NULL, 2, 'Trong ngành trồng trọt, xu hướng giảm tỉ trọng cây lương thực, tăng tỉ trọng cây công nghiệp, nhằm để:', 'a:4:{i:0;a:3:{s:7:"content";s:50:"phát huy lợi thế về đất đai, khí hậu";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:47:"nâng cao hiệu quả kinh tế nông nghiệp";s:7:"correct";i:1;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:58:"chuyển nền nông nghiệp sang sản xuất hàng hóa";s:7:"correct";i:0;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:43:"tạo ra nhiều sản phẩm xuất khẩu";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 4, NULL, NULL),
