(743, 15, NULL, 2, 'Xu hướng chuyển dịch cơ cấu kinh tế nào sau đây không đúng với khu vực II (công nghiệp - xây dựng) ở nước ta?', 'a:4:{i:0;a:3:{s:7:"content";s:153:"Hình thành và phát triển các ngành công nghệ cao như: sản xuất ô tô, thiết bị chính xác, máy móc điện tử và viễn thông";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:141:"Phát triển các ngành chủ lực như: chế biến thực phẩm, dệt, da giày, may mặc, sản xuất hóa chất, cao su, plastic";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:119:"Giảm tỉ trọng nhóm ngành công nghiệp chế biến, tăng tỉ trọng nhóm ngành công nghiệp khai thác";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:172:"Tăng tỉ trọng các sản phẩm có khả năng cạnh tranh về chất lượng và giá cả, giảm tỉ trọng các sản phẩm ít có khả năng cạnh tranh";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 247, NULL, NULL),
(744, 15, NULL, 2, 'Cơ cấu sản phẩm công nghiệp đang chuyển đổi theo hướng:', 'a:4:{i:0;a:3:{s:7:"content";s:100:"tăng tỉ trọng sản phẩm trung bình, giảm tỉ trọng sản phẩm chất lượng thấp";s:7:"correct";i:0;s:5:"order";i:0;}i:1;a:3:{s:7:"content";s:99:"tăng  tỉ trọng sản phẩm cao cấp, trung bình; giảm sản phẩm chất lượng thấp";s:7:"correct";i:0;s:5:"order";i:1;}i:2;a:3:{s:7:"content";s:108:"tăng tỉ trọng của các sản phẩm cao cấp, giảm các loại sản phẩm chất lượng thấp";s:7:"correct";i:1;s:5:"order";i:2;}i:3;a:3:{s:7:"content";s:103:"giảm tỉ trọng sản phẩm cao cấp, tăng tỉ trọng sản phẩm chất lượng trung bình";s:7:"correct";i:0;s:5:"order";i:3;}}', 0, 65535, 167, NULL, NULL),
